import React, { useReducer } from 'react';
import Home from './Home';
import { MdAdd } from "react-icons/md";

export default function Screens() {
    const [windows, dispatch] = useReducer(windowsReducer, ['0-home', '0-home', '0-home', '0-home']);
    return (
        <>
            <div style={{
                position: 'absolute',
                top: '0',
                left: '0'
            }}>
                <MdAdd />
            </div>

            <div class="screen" style={{
                gridRowStart: 'a0',
                gridRowEnd: (windows[0] === windows[2] ? 'a2' : 'a0'),
                gridColumnStart: 'a0',
                gridColumnEnd: (windows[0] === windows[1] ? 'a1' : 'a0')
            }}>
                {windows[0].split('-')[1] === 'home' && <Home></Home>}
            </div>

            {windows[0] !== windows[1] && <div class="screen" style={{
                gridRowStart: 'a1',
                gridRowEnd: (windows[1] === windows[3] ? 'a3' : 'a1'),
                gridColumnStart: 'a1',
                gridColumnEnd: 'a1'
            }}>
                {windows[1].split('-')[1] === 'home' && <Home></Home>}
            </div>}

            {windows[0] !== windows[2] && <div class="screen" style={{
                gridRowStart: 'a2',
                gridRowEnd: 'a2',
                gridColumnStart: 'a2',
                gridColumnEnd: (windows[2] === windows[3] ? 'a3' : 'a2')
            }}>
                {windows[2].split('-')[1] === 'home' && <Home></Home>}
            </div>}

            {windows[1] !== windows[3] && windows[2] !== windows[3] && <div class="screen" style={{
                gridRowStart: 'a3',
                gridRowEnd: 'a3',
                gridColumnStart: 'a3',
                gridColumnEnd: 'a3'
            }}>
                {windows[3].split('-')[1] === 'home' && <Home></Home>}
            </div>}
        </>
    );
}

function windowsReducer(windows, action) {
    const time = new Date().getMilliseconds;
    switch (action.type) {
      case 'add': {
        switch (action.position) {
          case 0: {
            if (windows[0] === windows[1] === windows[2] === windows[3]) {
              return windows.with(0, time + '-' + action.screen).with(2, time + '-' + action.screen);
            } else if (windows[2] === windows[0] || windows[1] === windows[0]) {
              return windows.with(0, time + '-' + action.screen);
            } else {
              throw Error('Should use `exchange` instead of `add` in windowsReducer');
            }
          }
          case 1: {
            if (windows[0] === windows[1] === windows[2] === windows[3]) {
              return windows.with(1, time + '-' + action.screen).with(3, time + '-' + action.screen);
            } else if (windows[0] === windows[1] || windows[3] === windows[1]) {
              return windows.with(1, time + '-' + action.screen);
            } else {
              throw Error('Should use `exchange` instead of `add` in windowsReducer');
            }
          }
          case 2: {
            if (windows[0] === windows[1] === windows[2] === windows[3]) {
              return windows.with(2, time + '-' + action.screen).with(3, time + '-' + action.screen);
            } else if (windows[0] === windows[2] || windows[3] === windows[2]) {
              return windows.with(2, time + '-' + action.screen);
            } else {
              throw Error('Should use `exchange` instead of `add` in windowsReducer');
            }
          }
          case 3: {
            if (windows[0] === windows[1] === windows[2] === windows[3]) {
              return windows.with(1, time + '-' + action.screen).with(3, time + '-' + action.screen);
            } else if (windows[1] === windows[3] || windows[2] === windows[3]) {
              return windows.with(3, time + '-' + action.screen);
            } else {
              throw Error('Should use `exchange` instead of `add` in windowsReducer');
            }
          }
          default: {
            throw Error('Unknown position in windowsReducer add operation: ' + action.position);
          }
        }
      }
      case 'remove': {
        //If all windows gone then go to home
        return windows.with(action.position, "");
      }
      default: {
        throw Error('Unknown action in windowsReducer: ' + action.type);
      }
    }
  }
