import React, { useReducer } from 'react';
import { MdAdd, MdOutlineSwapHoriz, MdClose } from "react-icons/md";

export default function ScreensMenus({windowsDispatch}) {
    return (
        <>
            <MenuButtonCont position={{
                gridRowStart: 'a0',
                gridColumnStart: 'a0'
            }}>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'remove',
                    position: 0
                })}}>
                    <MdClose />
                </MenuButton>
                <MenuButton>
                    <MdOutlineSwapHoriz />
                </MenuButton>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'add',
                    position: 0,
                    screen: 'home'
                })}}>
                    <MdAdd />
                </MenuButton>
                <div>
                    <button>Phonology</button>
                </div>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowStart: 'a1',
                gridColumnEnd: 'a1',
                marginLeft: 'auto'
            }}>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'remove',
                    position: 1
                })}}>
                    <MdClose />
                </MenuButton>
                <MenuButton>
                    <MdOutlineSwapHoriz />
                </MenuButton>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'add',
                    position: 1,
                    screen: 'home'
                })}}>
                    <MdAdd />
                </MenuButton>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowEnd: 'a2',
                gridColumnStart: 'a2',
                marginTop: 'auto'
            }}>
                <MenuButtons></MenuButtons>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowEnd: 'a3',
                gridColumnEnd: 'a3',
                marginTop: 'auto',
                marginLeft: 'auto'
            }}>
                <MenuButtons></MenuButtons>
            </MenuButtonCont>
        </>
    );
}

function MenuButtonCont({position, children}) {
    return (
        <div>
            <div style={{
                ...position,
                width: 'min-content',
                height: 'min-content',
                display: 'flex',
                flexDirection: 'column',
                backgroundColor: 'darkgray',
                zIndex: '2'
            }}>
                {children}
            </div>
        </div>
    );
}

function MenuButton({onClick, children}) {
    return (
        <button onClick={onClick} style={{
            backgroundColor: 'transparent',
            border: 'none',
            margin: '0',
            padding: '0.3em'
        }}>
            {children}
        </button>
    );
}

function MenuButtons({position, windowsDispatch}) {
    return (
        <>
            <MenuButton onClick={() => {windowsDispatch({
                type: 'remove',
                position: position
            })}}>
                <MdClose />
            </MenuButton>
            <MenuButton>
                <MdOutlineSwapHoriz />
            </MenuButton>
            <MenuButton onClick={() => {windowsDispatch({
                type: 'add',
                position: position,
                screen: 'home'
            })}}>
                <MdAdd />
            </MenuButton>
        </>
    );
}
