import React, { useReducer } from 'react';
import { MdAdd, MdOutlineSwapHoriz, MdClose } from "react-icons/md";

export default function ScreensMenus({windowsDispatch}) {
    return (
        <>
            <MenuButtonCont position={{
                gridRowStart: 'a0',
                gridColumnStart: 'a0'
            }}>
                <MenuButton>
                    <MdClose />
                </MenuButton>
                <MenuButton>
                    <MdOutlineSwapHoriz />
                </MenuButton>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'add',
                    position: 0,
                    screen: 'home'
                })}}>
                    <MdAdd />
                </MenuButton>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowStart: 'a1',
                gridColumnEnd: 'a1',
                marginLeft: 'auto'
            }}>
                <MenuButton>
                    <MdClose />
                </MenuButton>
                <MenuButton>
                    <MdOutlineSwapHoriz />
                </MenuButton>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'add',
                    position: 1,
                    screen: 'home'
                })}}>
                    <MdAdd />
                </MenuButton>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowEnd: 'a2',
                gridColumnStart: 'a2',
                marginTop: 'auto'
            }}>
                <MenuButton>
                    <MdClose />
                </MenuButton>
                <MenuButton>
                    <MdOutlineSwapHoriz />
                </MenuButton>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'add',
                    position: 2,
                    screen: 'home'
                })}}>
                    <MdAdd />
                </MenuButton>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowEnd: 'a3',
                gridColumnEnd: 'a3',
                marginTop: 'auto',
                marginLeft: 'auto'
            }}>
                <MenuButton>
                    <MdClose />
                </MenuButton>
                <MenuButton>
                    <MdOutlineSwapHoriz />
                </MenuButton>
                <MenuButton onClick={() => {windowsDispatch({
                    type: 'add',
                    position: 3,
                    screen: 'home'
                })}}>
                    <MdAdd />
                </MenuButton>
            </MenuButtonCont>
        </>
    );
}

function MenuButtonCont({position, children}) {
    return (
        <div style={{
            ...position,
            width: 'min-content',
            height: 'min-content',
            display: 'flex',
            flexDirection: 'column',
            backgroundColor: 'darkgray',
            zIndex: '2'
        }}>
            {children}
        </div>
    );
}

function MenuButton({onClick, children}) {
    return (
        <button onClick={onClick} style={{
            backgroundColor: 'transparent',
            border: 'none',
            margin: '0',
            padding: '0.3em'
        }}>
            {children}
        </button>
    );
}
