import React, { useReducer } from 'react';
import { MdAdd, MdOutlineSwapHoriz, MdClose } from "react-icons/md";

export default function ScreensMenus({windowsDispatch}) {
    return (
        <>
            <MenuButtonCont position={{
                gridRowStart: 'a0',
                gridColumnStart: 'a0'
            }}>
                <MenuButtons windowsDispatch={windowsDispatch} position={0}></MenuButtons>
                <Submenu></Submenu>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowStart: 'a1',
                gridColumnEnd: 'a1',
                marginLeft: 'auto'
            }}>
                <MenuButtons windowsDispatch={windowsDispatch} position={1}></MenuButtons>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowEnd: 'a2',
                gridColumnStart: 'a2',
                marginTop: 'auto'
            }}>
                <MenuButtons windowsDispatch={windowsDispatch} position={2}></MenuButtons>
            </MenuButtonCont>
            <MenuButtonCont position={{
                gridRowEnd: 'a3',
                gridColumnEnd: 'a3',
                marginTop: 'auto',
                marginLeft: 'auto'
            }}>
                <MenuButtons windowsDispatch={windowsDispatch} position={3}></MenuButtons>
            </MenuButtonCont>
        </>
    );
}

function MenuButtonCont({position, children}) {
    return (
        <div style={{
            ...position,
            width: 'min-content',
            height: 'min-content',
            display: 'flex',
            backgroundColor: 'darkgray',
            zIndex: '2'
        }}>
            {children}
        </div>
    );
}

function MenuButton({onClick, children}) {
    return (
        <button onClick={onClick} style={{
            backgroundColor: 'transparent',
            border: 'none',
            margin: '0',
            padding: '0.3em'
        }}>
            {children}
        </button>
    );
}

function MenuButtons({position, windowsDispatch}) {
    return (
        <div style={{
            display: 'flex',
            flexDirection: 'column',
        }}>
            <MenuButton onClick={() => {windowsDispatch({
                type: 'remove',
                position: position
            })}}>
                <MdClose />
            </MenuButton>
            <MenuButton>
                <MdOutlineSwapHoriz />
            </MenuButton>
            <MenuButton onClick={() => {windowsDispatch({
                type: 'add',
                position: position,
                screen: 'home'
            })}}>
                <MdAdd />
            </MenuButton>
        </div>
    );
}

function SubmenuButton({children}) {
    return (
        <button>
            {children}
        </button>
    );
}

function Submenu() {
    return (
        <div style={{
            display: 'flex',
            flexDirection: 'column'
        }}>
            <SubmenuButton>Phonology</SubmenuButton>
        </div>
    );
}
