import React, { useReducer } from 'react';
import logo from './logo.svg';
import './App.css';
import PageLayout from './PageLayout.js'
import Home from './Home.js';

export default function App() {
  const [windows, dispatch] = useReducer(windowsReducer, ['0-home', '1-home', '0-home', '1-home']);
  return (
    <PageLayout>
      {windows.map((i, index) => <div key={index.toString} style={{
        gridArea: 'a' + index,
        border: '5px solid green',
        padding: '0.5em',
        overflow: 'scroll'
      }}>
        {i === 'home' &&
          <Home></Home>
        }
      </div>)}
      <div class="screen" style={{
        gridRowStart: 'a0',
        gridRowEnd: (windows[0] === windows[2] ? 'a2' : 'a0'),
        gridColumnStart: 'a0',
        gridColumnEnd: (windows[0] === windows[1] ? 'a1' : 'a0')
      }}>
        {windows[0].split('-')[1] === 'home' && <Home></Home>}
      </div>

      {windows[0] !== windows[1] && <div class="screen" style={{
        gridRowStart: 'a1',
        gridRowEnd: (windows[1] === windows[3] ? 'a3' : 'a1'),
        gridColumnStart: 'a1',
        gridColumnEnd: 'a1'
      }}>
        {windows[1].split('-')[1] === 'home' && <Home></Home>}
      </div>}
    </PageLayout>
  );
}

function windowsReducer(windows, action) {
  switch (action.type) {
    case 'add': {
      return windows.with(action.position, action.newScreen);
    }
    case 'remove': {
      //If all windows gone then go to home
      return windows.with(action.position, "");
    }
    default: {
      throw Error('Unknown action in windowsReducer: ' + action.type);
    }
  }
}
